import React, { useState, useEffect } from "react";
import { getKnittingDetailsByRSN } from "../API/SampleApi";

const DisplayKnittingData = ({ RSN }) => {
  const [knittingData, setKnittingData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  useEffect(() => {
    if (RSN) {
      const fetchData = async () => {
        setLoading(true);
        try {
          const response = await getKnittingDetailsByRSN(RSN);
          setKnittingData(response.data); // Assuming the response contains knitting details
        } catch (err) {
          setError("Failed to fetch knitting details.");
        } finally {
          setLoading(false);
        }
      };

      fetchData();
    }
  }, [RSN]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  // Function to check for non-null and non-zero values
  const isNonZero = (value) => {
    if (value === null || value === undefined) return false;
    if (typeof value === "object" && value !== null) {
      // If it's an object, check the Weight and Time
      return (
        (value.Weight && value.Weight !== 0) ||
        (value.Time && value.Time !== 0)
      );
    }
    return value !== 0 && value !== ""; // Non-zero and non-empty values
  };

  // Filter out the fields with null or zero values
  const nonZeroData = knittingData
    ? Object.entries(knittingData).filter(([key, value]) => isNonZero(value))
    : [];

  return (
    <div>
      <h1>Knitting Details</h1>
      {nonZeroData.length > 0 ? (
        <table border="1" style={{ width: "100%", marginTop: "20px" }}>
          <thead>
            <tr>
              <th>Field</th>
              <th>Weight</th>
              <th>Time</th>
            </tr>
          </thead>
          <tbody>
            {nonZeroData.map(([key, value]) => {
              // If the value is an object (i.e., contains Weight and Time)
              if (typeof value === "object" && value !== null) {
                return (
                  <tr key={key}>
                    <td>{key}</td>
                    <td>{value.Weight || "N/A"}</td> {/* Render Weight */}
                    <td>{value.Time || "N/A"}</td> {/* Render Time */}
                  </tr>
                );
              }
              return (
                <tr key={key}>
                  <td>{key}</td>
                  <td colSpan="2">{value}</td> {/* Render non-object values */}
                </tr>
              );
            })}
          </tbody>
        </table>
      ) : (
        <p>No relevant data available</p>
      )}
    </div>
  );
};

export default DisplayKnittingData;
