import React, { useState, useEffect } from "react";
import {
  getKnittingDetailsByRSN,
  updateKnittingDetails,
} from "../API/Api"; // Import the api functions

const UpdateKnittingDetails = () => {
  const [RSN, setRSN] = useState(""); // Track the RSN input
  const [knittingDetails, setKnittingDetails] = useState(null); // Store knitting details
  const [updatedData, setUpdatedData] = useState({
    Size: "",
    FrontRight: "",
    FrontLeft: "",
    FrontComplete: "",
    // Add other fields...
  });

  // Fetch knitting details by RSN
  const fetchKnittingDetails = async () => {
    if (RSN) {
      try {
        const data = await getKnittingDetailsByRSN(RSN);
        setKnittingDetails(data.data); // Store data in state
      } catch (error) {
        alert("Error fetching knitting details!");
      }
    }
  };

  // Update knitting details by RSN
  const handleUpdateKnittingDetails = async () => {
    try {
      const data = await updateKnittingDetails(RSN, updatedData);
      alert(data.message); // Show success message
      fetchKnittingDetails(); // Re-fetch updated details
    } catch (error) {
      alert("Error updating knitting details!");
    }
  };

  useEffect(() => {
    if (RSN) {
      fetchKnittingDetails(); // Fetch knitting details when RSN changes
    }
  }, [RSN]);

  return (
    <div>
      <h1>Knitting Details</h1>
      <div>
        <input
          type="text"
          value={RSN}
          onChange={(e) => setRSN(e.target.value)}
          placeholder="Enter RSN"
        />
        <button onClick={fetchKnittingDetails}>Fetch Knitting Details</button>
      </div>

      {knittingDetails && (
        <div>
          <h2>Knitting Record for RSN: {RSN}</h2>
          <p>Size: {knittingDetails.Size}</p>
          <p>FrontRight: {JSON.stringify(knittingDetails.FrontRight)}</p>
          {/* Render other fields similarly */}

          <h3>Update Knitting Details</h3>
          <input
            type="text"
            value={updatedData.Size}
            onChange={(e) => setUpdatedData({ ...updatedData, Size: e.target.value })}
            placeholder="Update Size"
          />
          {/* Add inputs for other fields */}
          <button onClick={handleUpdateKnittingDetails}>Update Details</button>
        </div>
      )}
    </div>
  );
};

export default UpdateKnittingDetails;
