import React, { useState, useEffect } from "react";
import { getColorDetailByColorId } from "../API/ColorDetailApi";

const ShowColorDetail = ({ ColorId }) => {
    console.log(ColorId);
  const [colorDetail, setColorDetail] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  useEffect(() => {
    if (ColorId) {
      const fetchData = async () => {
        setLoading(true);
        try {
          const response = await getColorDetailByColorId(ColorId);
          setColorDetail(response.data); // Store color detail data
        } catch (err) {
          setError("Failed to fetch color details.");
        } finally {
          setLoading(false);
        }
      };

      fetchData();
    }
  }, [ColorId]); // Trigger effect whenever ColorId changes

  if (loading) return <p>Loading...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  // Dynamically create headers from the keys of the colorDetail object
  const headers = colorDetail ? Object.keys(colorDetail) : [];

  return (
    <div>
      <h1>Color Detail for Color ID: {ColorId}</h1>
      {colorDetail ? (
        <div>
          <h2>Details</h2>
          <table border="1" style={{ width: "100%", marginTop: "20px" }}>
            <thead>
              <tr>
                {headers.map((header, index) => (
                  <th key={index}>{header}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              <tr>
                {headers.map((header, index) => (
                  <td key={index}>{colorDetail[header] || "N/A"}</td>
                ))}
              </tr>
            </tbody>
          </table>
        </div>
      ) : (
        <p>No color details available for this Color ID.</p>
      )}
    </div>
  );
};

export default ShowColorDetail;
